INSERT INTO question(id, topic, answer, images, options, subtitle, initial_text, text, number, test_id) values (2421, 'Matemática','E', '', '||A:43%#@||B:57,5%#@||C:61,5%#@||D:73%#@||E:77%#@', '', '', 'Leia o texto a seguir:   “A cidade de São Paulo atingiu 23% da meta de vacinação contra sarampo entre os jovens de 15 a 29 anos. Segundo a Secretaria Municipal de Saúde, de 10 de junho até esta quintafeira (25), foram aplicadas 720 mil doses da vacina.”.  Na cidade de São Paulo, após 25 de julho, foram vacinados 50% dos jovens de 15 a 19 anos que ainda não haviam recebido a vacina. Qual é a porcentagem total de jovens vacinados até essa data?', '19', 71);
