INSERT INTO question(id, topic, answer, images, options, subtitle, initial_text, text, number, test_id) values (2423, 'Matemática','C', 'https://i.imgur.com/WeQjX8l.png', '||A:2 + √3#@||B:1/(2 − √3)#@||C:2 − √3#@||D:√3 − 1#@||E:#@', '', '', 'Na  figura  a  seguir,  temos  três  circunferências  de  raio 1,  tangentes  entre  si  e  inscritas  no  retângulo ABCD. Sabendo que M é ponto do segmento AD e que F, G e E são pontos de tangência entre as circunferências e os lados do retângulo, calcule o valor da tangente do ângulo MEF.', '21', 71);
