INSERT INTO question(id, topic, answer, images, options, subtitle, initial_text, text, number, test_id) values (2691, 'Física','C', '', '||A:Gasolina, pois gera mais energia com sua combustão.#@||B:Álcool, pois é mais barato.#@||C:Álcool, pois um volume equivalente a R$3,999 apresenta maior energia de combustão que 7200 kcal.#@||D:Gasolina, pois a energia de 70% do seu volume é maior que 4600 kcal.#@', '', 'Em um posto de combustível, o preço do litro da gasolina é de R$3,999 e do álcool, R$2,499. Sabendo que os poderes caloríficos típicos e aproximados da gasolina e do álcool são, respectivamente, 7200 e 4600 kcal/litro, qual das alternativas abaixo apresenta o combustível com melhor custo-benefício e sua respectiva justificativa?', '', '50', 77);
